mapper.update:
UPDATE yankuang_equipment.el_authority
status = 99, update_at = unix_timestamp(now())
WHERE id = #{id}

mapper.select:
SELECT 
FROM yankuang_equipment.el_authority
WHERE
    1 = 1

        AND code = #{code}


        AND name = #{name}


        AND url = #{url}


        AND type = #{type}


        AND create_by = #{createBy}


        AND remark = #{remark}


        AND sorting = #{sorting}


        AND update_at = #{updateAt}


        AND update_by = #{updateBy}


        AND version = #{version}


        AND create_at = #{createAt}


        AND id = #{id}

    AND status != 99
order by create_at desc LIMIT #{offset}, #{limit}

SELECT count(1) FROM yankuang_equipment.el_authority
WHERE
    1 = 1

        AND code = #{code}


        AND name = #{name}


        AND url = #{url}


        AND type = #{type}


        AND create_by = #{createBy}


        AND remark = #{remark}


        AND sorting = #{sorting}


        AND update_at = #{updateAt}


        AND update_by = #{updateBy}


        AND version = #{version}


        AND create_at = #{createAt}


        AND id = #{id}

    AND status != 99
