mapper.update:
UPDATE el_use
SET is_del = 99
WHERE id = #{id,jdbcType=BIGINT}

mapper.select:
SELECT 
FROM el_use
WHERE 1=1

    AND approve_code = #{approveCode,jdbcType=VARCHAR}


    AND code = #{code,jdbcType=VARCHAR}


    AND sorting = #{sorting,jdbcType=VARCHAR}


    AND is_del = #{isDel,jdbcType=TINYINT}


    AND status = #{status,jdbcType=VARCHAR}


    AND use_by = #{useBy,jdbcType=BIGINT}


    AND use_at = #{useAt,jdbcType=TIMESTAMP}


    AND use_position = #{usePosition,jdbcType=VARCHAR}


    AND use_equipment_type = #{useEquipmentType,jdbcType=VARCHAR}


    AND approve_by = #{approveBy,jdbcType=BIGINT}


    AND approve_at = #{approveAt,jdbcType=TIMESTAMP}


    AND approve_opinion = #{approveOpinion,jdbcType=VARCHAR}


    AND create_by = #{createBy,jdbcType=BIGINT}


    AND create_at = #{createAt,jdbcType=TIMESTAMP}


    AND update_by = #{updateBy,jdbcType=BIGINT}


    AND update_at = #{updateAt,jdbcType=TIMESTAMP}


    AND version = #{version,jdbcType=VARCHAR}


    AND remarks = #{remarks,jdbcType=VARCHAR}

AND is_del != 99
ORDER BY create_at DESC

mapper.update:
UPDATE el_use
SET status = 2
WHERE id = #{id,jdbcType=BIGINT}
